package me.weishu.reflection

import android.content.Context
import android.os.Build.VERSION
import android.os.StrictMode
import android.os.StrictMode.VmPolicy
import android.text.TextUtils
import android.util.Base64
import dalvik.system.DexFile
import java.io.File
import java.io.FileOutputStream
import java.util.*

/** Original project: https://github.com/tiann/FreeReflection
 * @author weishu
 * @date 2018/6/7.
 */
object Reflection {
    private const val DEX =
        ""

    private val alreadyUnseal = HashSet<String>()
    fun unsealAll(context: Context): Int {
        if (VERSION.SDK_INT < 28) {
            // Below Android P, ignore
            return 0
        }
        StrictMode.setVmPolicy(
            VmPolicy.Builder(StrictMode.getVmPolicy()).permitNonSdkApiUsage().build()
        )
        // try exempt API first.
        if (BootstrapClass.exemptAll()) {
            return 0
        }
        return if (unsealByDexFile(context, null)) {
            0
        } else -1
    }

    @JvmStatic
    fun unseal(context: Context, packages: List<String>): Int {
        if (VERSION.SDK_INT < 28) {
            // Below Android P, ignore
            return 0
        }
        val strings: MutableList<String> = ArrayList()
        for (p in packages) {
            var pkg = p
            if (!pkg.isNullOrEmpty()) {
                if (pkg.endsWith("/")) pkg = pkg.substring(0, pkg.length - 1)
                strings.add("L" + pkg.replace(".", "/"))
            }
        }
        strings.removeAll(alreadyUnseal)
        if(strings.isEmpty())//already unsealed
            return 0

        alreadyUnseal.addAll(strings)

        val methods = strings.toTypedArray()
        StrictMode.setVmPolicy(
            VmPolicy.Builder(StrictMode.getVmPolicy()).permitNonSdkApiUsage().build()
        )
        // try exempt API
        if (BootstrapClass.exempt(*methods)) {
            return 0
        }
        return if (unsealByDexFile(context, methods)) {
            0
        } else -1
    }

    private fun unsealByDexFile(context: Context, methods: Array<String>?): Boolean {
        val codeCacheDir = context.cacheDir
        if (!codeCacheDir.exists()) codeCacheDir.mkdirs()
        val code = File(codeCacheDir, System.currentTimeMillis().toString() + ".dex")
        return try {
            FileOutputStream(code).use { fos -> fos.write(Base64.decode(DEX, Base64.NO_WRAP)) }
            val dexFile = DexFile(code)
            val bootstrapClass = dexFile.loadClass(BootstrapClass::class.java.canonicalName, null)
            if (methods == null) {
                val exemptAll = bootstrapClass.getDeclaredMethod("exemptAll")
                exemptAll.invoke(null) as Boolean
            } else {
                val exemptAll =
                    bootstrapClass.getDeclaredMethod("exempt", Array<String>::class.java)
                exemptAll.invoke(null, *arrayOf<Any>(methods)) as Boolean
            }
        } catch (e: Throwable) {
            e.printStackTrace()
            false
        } finally {
            if (code.exists()) {
                code.delete()
            }
        }
    }
}